import React, { useState, useEffect } from "react";
import { io } from "socket.io-client";

const socket = io("http://localhost:3001");

function App() {
  const urlParams = new URLSearchParams(window.location.search);
  const initialRoom = urlParams.get("room") || "";
  const [roomCode, setRoomCode] = useState(initialRoom);
  const [inRoom, setInRoom] = useState(false);
  const [playerList, setPlayerList] = useState([]);
  const [playerName, setPlayerName] = useState("");
  const [gameStarted, setGameStarted] = useState(false);
  const [myRoles, setMyRoles] = useState([]);


  useEffect(() => {
    socket.on("connect", () => {
      console.log("Connected to server:", socket.id);
    });

    socket.on("roomPlayers", (names) => {
      setPlayerList(names);
    });

    socket.on("gameStarted", () => {
      setGameStarted(true);
    });

    return () => {
      socket.off("gameStarted");
      socket.off("roomPlayers");
    };

    socket.on("yourRoles", (roles) => {
  setMyRoles(roles);
  socket.off("yourRoles");
});
  }, []);

  const createRoom = () => {
    socket.emit("createRoom", { name: playerName }, (response) => {
      if (response.success) {
        setRoomCode(response.roomCode);
        setInRoom(true);
      }
    });
  };

  const joinRoom = () => {
    socket.emit("joinRoom", { roomCode, name: playerName }, (response) => {
      if (response.success) {
        setInRoom(true);
      } else {
        alert(response.message);
      }
    });
  };

  if (inRoom) {
    return (
      <div>
        <h2>In Room: {roomCode}</h2>
        <h3>Players:</h3>
        <ul>
          {playerList.map((name, idx) => (
            <li key={idx}>{name}</li>
          ))}
        </ul>
        <input
  type="text"
  value={`${window.location.origin}?room=${roomCode}`}
  readOnly
  onClick={(e) => e.target.select()}
/>

        {gameStarted && (
  <>
    <h3>Game Started!</h3>
    <h4>Your Roles:</h4>
    <ul>
      {myRoles.map((role, idx) => (
        <li key={idx}>{role}</li>
      ))}
    </ul>
  </>
)}

        {gameStarted && <h3>Game Started!</h3>}
      </div>
    );

  }

  return (
    <div>
      <h1>Coup Clone</h1>

      <input
        placeholder="Your Name"
        value={playerName}
        onChange={(e) => setPlayerName(e.target.value)}
      />
      <br /><br />

      <button onClick={createRoom}>Create Room</button>
      <br /><br />

      <input
        placeholder="Enter Room Code"
        value={roomCode}
        onChange={(e) => setRoomCode(e.target.value)}
      />
      <button onClick={joinRoom}>Join Room</button>
    </div>
  );
}

export default App;
